// Set Body Margin Bottom for Dynamic Sized (any height!) Sticky Bottom Footer
var stickyFooter = function () {
  $('body').css('margin-bottom', $('footer').outerHeight());
},
    didResize = false;

stickyFooter();

$(window).resize(function () {
  didResize = true;
});

setInterval(function () {
  if (didResize) {
    didResize = false;
    stickyFooter();
  }
}, 250);  // Keep this interval for RWD purposes (window resizing or device orientation changes) 